package cn.easy.android.sample.net.communication;import java.util.List;import org.apache.http.Header;import org.apache.http.HttpEntity;import android.content.Context;import cn.easy.android.library.net.BaseCommunication;import cn.easy.android.sample.app.EasySampleApplication;import cn.easy.android.sample.app.EasySampleHttpApi;import cn.easy.android.sample.app.EasySampleHttpProxy;import com.loopj.android.http.RequestHandle;import com.loopj.android.http.RequestParams;import com.loopj.android.http.ResponseHandlerInterface;public class ShotsCommunication extends BaseCommunication {	private int mPage = 1;	private int mPerPage = 15;	private String mCategory;	public ShotsCommunication(Context context,			ResponseHandlerInterface responseHandler, String category,			int page, int perPage) {		super(context, responseHandler);		mCategory = category;		mPage = page;		mPerPage = perPage;	}	@Override	public String getURL() {		RequestParams params = new RequestParams();		params.put("page", mPage);		params.put("per_page", mPerPage);		return EasySampleHttpApi.fullUrl(				String.format(EasySampleHttpApi.URL_SHORT_DEBUTS, mCategory),				params);	}	@Override	public List<Header> getRequestHeadersList() {		return null;	}	@Override	public HttpEntity getRequestEntity() {		return null;	}	@Override	public RequestHandle execute(boolean shouldUseAysnc) {		EasySampleHttpProxy proxy = (EasySampleHttpProxy) EasySampleApplication				.getApplication(mContext).getHttpProxy();		proxy.get(mContext, getURL(), getRequestHeaders(),				getResponseHandler(), shouldUseAysnc);		return null;	}}