package cn.easy.android.library.net;import java.util.List;import org.apache.http.Header;import org.apache.http.HttpEntity;import android.content.Context;import com.loopj.android.http.RequestHandle;import com.loopj.android.http.ResponseHandlerInterface;/** * Title: ICommunication</p> * Description: http会话基类</p> * @author lin.xr * @date 2014-6-28 下午11:01:15 */public abstract class BaseCommunication {	/** POST请求实体内容类型 */	public static String POST_CONTENT_TYPE = null;	static final String TAG = "ICommunication";	protected Context mContext;	protected ResponseHandlerInterface mResponseHandler;	/**	 * 获取会话请求url地址	 * 	 * @return 会话请求url地址	 */	public abstract String getURL();	/**	 * 获取会话请求头列表	 * 	 * @return 会话请求头列表	 */	public abstract List<Header> getRequestHeadersList();	/**	 * 获取会话请求实体	 * 	 * @return 会话请求实体	 */	public abstract HttpEntity getRequestEntity();	/**	 * 执行会话请求	 * 	 * @param shouldUseAysnc	 *            是否线程异步	 * @return 会话请求回调	 */	public abstract RequestHandle execute(boolean shouldUseAysnc);	public BaseCommunication(Context context,			ResponseHandlerInterface responseHandler) {		mContext = context;		mResponseHandler = responseHandler;	}	/**	 * 获取会话请求头	 * 	 * @return 会话请求头	 */	public Header[] getRequestHeaders() {		List<Header> headers = getRequestHeadersList();		if (headers == null || headers.isEmpty()) {			return new Header[] {};		}		return headers.toArray(new Header[headers.size()]);	}	/**	 * 获取会话请求响应回调	 * 	 * @return 会话请求响应回调	 */	public ResponseHandlerInterface getResponseHandler() {		return mResponseHandler;	}}