package cn.easy.android.library.task;import android.os.Process;/** * Title: AbsBackgroudRunnable</p> * Description: 后台优先级任务抽象类</p> * @author lin.xr * @date 2014-6-28 下午8:07:16 */public abstract class AbsBackgroudRunnable implements Runnable {	static final String TAG = "AbsBackgroudRunnable";		public abstract void runImpl();	public final void run() {		Process.setThreadPriority(Process.THREAD_PRIORITY_BACKGROUND);		runImpl();	}	protected boolean isInterrupted() {		return Thread.currentThread().isInterrupted();	}}