package cn.easy.android.library.util;import java.text.SimpleDateFormat;import java.util.Date;import java.util.Locale;/** * Title: DateUtils</p> * Description: 时间操作工具类</p> * @author lin.xr * @date 2014-6-29 上午12:25:43 */public class DateUtils {	/** yyyy-MM-dd HH:mm:ss */	public static SimpleDateFormat SDF_YMD_HMS = new SimpleDateFormat(			"yyyy-MM-dd HH:mm:ss", Locale.getDefault());	/** yyyy-MM-dd */	public static SimpleDateFormat SDF_YMD = new SimpleDateFormat("yyyy-MM-dd",			Locale.getDefault());	static final String TAG = "DateUtils";	/**	 * 获取长整型时间戳转换成日期时间表示的字符串	 * 	 * @param milliseconds	 *            毫秒数	 * @param format	 *            日期时间格式化	 * @return 日期时间表示的字符串	 */	public static String fmtTimeMillsToString(long milliseconds,			SimpleDateFormat format) {		Date date = new Date(milliseconds);		return fmtDateToString(date, format);	}	/**	 * 获取日期时间转换成日期时间表示的字符串	 * 	 * @param date	 *            日期时间	 * @param format	 *            日期时间格式化	 * @return 日期时间表示的字符串	 */	public static String fmtDateToString(Date date, SimpleDateFormat format) {		return format.format(date);	}}