package cn.easy.android.library.util;import java.io.IOException;import java.io.InputStream;import java.io.OutputStream;/** * Title: IOUtils</p> * Description: IO操作工具类</p> * @author lin.xr * @date 2014-6-28 上午12:41:34 */public class IOUtils {	static final String TAG = "IOUtils";	/**	 * A utility function to close an input stream without raising an exception.	 * 	 * @param is	 *            input stream to close safely	 */	public static void silentCloseInputStream(InputStream is) {		try {			if (is != null) {				is.close();			}		} catch (IOException e) {			EvtLog.w(TAG, e);		}	}	/**	 * A utility function to close an output stream without raising an	 * exception.	 * 	 * @param os	 *            output stream to close safely	 */	public static void silentCloseOutputStream(OutputStream os) {		try {			if (os != null) {				os.close();			}		} catch (IOException e) {			EvtLog.w(TAG, e);		}	}}